// Global type declarations for ESLint

// Browser APIs
declare const setTimeout: typeof globalThis.setTimeout;
declare const clearTimeout: typeof globalThis.clearTimeout;
declare const btoa: typeof globalThis.btoa;
declare const atob: typeof globalThis.atob;
declare const crypto: typeof globalThis.crypto;

// Jest globals
declare const jest: any;

// HTML Element types
declare const HTMLInputElement: typeof globalThis.HTMLInputElement;
declare const HTMLDivElement: typeof globalThis.HTMLDivElement;
declare const HTMLSelectElement: typeof globalThis.HTMLSelectElement;
